import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
<header>
  <div class="container">
    <nav class="menu">
      <img src="../assets/images/logo-form.png" alt="logo do FormsDevHelp" class="logo-header">
      <ul>
        <li><a href="/">Início</a></li>
        <li><a href="/helper">Documentação</a></li>
        <li><a href="/forms">FormsDevHelp</a></li>
      </ul>
    </nav>
  </div>
</header>
